import Dialog from "@mui/material/Dialog";
import "../Dashboard.css";
import Transfer from "../images/transfer.png";
import { useState } from "react";
import { VIEW } from "../Constants/ViewReceipts";
import React from "react";

const ReceiptDialog = ({ open, handleClose, openSignup }) => {

    const [dataSet, setDataSet] = useState({
        firstName: "",
        lastName: "",
        email: "",
        index: "",
        phone: "",
        address: "",
      });

      const handleData = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        var newDataSet = JSON.parse(JSON.stringify(dataSet));
        newDataSet[name] = value;
        setDataSet(newDataSet);
      };

  return (
    <div>
      <Dialog
        classes={{ paper: "receipts_dialog" }}
        open={open}
        onClose={handleClose}
      >
        <div className="receipts_image_main">
          <img className="receipts_image" src={Transfer} alt="firespot" />
        </div>
        <div className="title-heading">
          <div className="sign-in">Payment Successfull !!!</div>
        </div>
        <div className="receipts_dialog_main">
          <div className="profile_form_main">
            {VIEW.map((field) => {
              if (field.type === "select") {
                return (
                  <div className="form-row" key={field.id}>
                    <div className="form-label">{field.label}</div>
                    <select
                      className={field.className}
                      disabled={field.disabled}
                    >
                      {field.options.map((option) => {
                        return (
                          <option key={option.id} value={option.id}>
                            {option.label}
                          </option>
                        );
                      })}
                    </select>
                  </div>
                );
              } else {
                return (
                  <div className="form-row-flex" key={field.left.id}>
                    <div className="form_row-left">
                      <div className="form-label">{field.left.label}</div>
                      <input
                        placeholder={field.left.label}
                        name={field.left.name}
                        className={field.left.className}
                        type={field.left.type}
                        disabled={field.left.disabled}
                      />
                    </div>
                    <div className="form_row-right">
                      <div className="form-label">{field.right.label}</div>
                      <input
                        placeholder={field.right.label}
                        name={field.right.name}
                        className={field.right.className}
                        type={field.right.type}
                        disabled={field.right.disabled}
                      />
                    </div>
                  </div>
                );
              }
            })}
          </div>
          <div className="print_close">
            <button className="print_button">Print</button>
            <div>
              <button className="print_button" onClick={openSignup}>
                Close
              </button>
            </div>
          </div>
        </div>
      </Dialog>
    </div>
  );
};
export default ReceiptDialog;
