import * as React from "react";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import "../Dashboard.css";
import Student from "../images/Student.png";
import { CREATE } from "../Constants/CreateForm";


const Create = ({ open, handleClose, onSubmit }) => {
  const [user, setUser] = React.useState("");
  const [password, setPassword] = React.useState("");

  const [dataSet, setDataSet] = useState({
    firstName: "",
    lastName: "",
    email: "",
    contactNo: "",
    courseOfStudy: "",
    academicYear: "",
    faculty: "",
    department: "",
  });

  const handleData = (isSubmit) => {
    setUser("");
    setPassword("");

    if (isSubmit) {
      if (user === "" || password === "") {
        console.log("Please Fill the Form");
      } else {
        onSubmit(user, password);
      }
    } else {
      handleClose();
    }
  };

  return (
    <div>
      <Dialog
        classes={{ paper: "submit-form" }}
        open={open}
        onClose={handleClose}
      >
        <img className="dialog-title" src={Student} alt="firespot" />
        <div className="title-heading">
          <div className="sign-in">Sign Up</div>
          <div className="uom-user">UOM User</div>
        </div>
        <div>
          <hr />
        </div>

        {CREATE.map((field) => {
        if (field.show) {
          if (field.type === "select") {
            return (
              <div className="form-row" key={field.id}>
                <div className="form-label">{field.label}</div>
              </div>
            );
          } else {
            return (
              <div className="form-row" key={field.id}>
                <div className="form-label">{field.label}</div>
                <input
                  placeholder={field.label}
                  name={field.name}
                  className={field.className}
                  type={field.type}
                  disabled={field.disabled}
                  onChange={(e) => handleData(e)}
                  value={dataSet[field.name]}
                />
              </div>
            );
          }
        } else {
          return null;
        }
      })}

        <div className="signin-text">
          <Button className="signin" onClick={() => handleData(true)}>
            Sign Up
          </Button>
        </div>
      </Dialog>
    </div>
  );
};
export default Create;
